	/**
	 *
	 * Shortcode output
	 * 
	 * @return string
	 * 
	 */
	public function out( $atts, $content = '' ) {
		$atts = Codevz_Plus::shortcode_atts( $this, $atts );

		// Classes
		$classes = array();
		$classes[] = 'cz_svg clr';

		// Styles
		$s = $atts['width'] ? 'width:' . $atts['width'] . ';' : '';
		$s .= $atts['pos'] ? 'background-position-y:' . $atts['pos'] . ';' : '';
		$s .= $atts['anim_delay'] ? 'animation-delay:' . $atts['anim_delay'] . ';' : '';

		// Out
		$out = '<div' . Codevz_Plus::classes( $atts, $classes ) . ' style="background-image: url(' . Codevz_Plus::get_image( $atts['img'], 0, 1 ) . ');' . $s . '">' . self::svg( $atts['svg'], $atts['fill'] ) . '</div>';

		return Codevz_Plus::_out( $atts, $out, false, $this->name );
	}

